public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 for(int v=0;v<=10;v++)
 if(v%2==0){
System.out.println("The Number "+v+" is even");
}
else {
	System.out.println("The Number "+v+" is odd");
}

	
	}

}
